package com.cheesecake.tickters.viewmodel

import androidx.lifecycle.ViewModel
import com.cheesecake.tickters.viewmodel.state.MovieUIState
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.update

@HiltViewModel
class MovieViewModel : BaseViewModel<MovieUIState>(MovieUIState()) {
    private val items = listOf(
        "data:image/jpeg;base64,",
    )

    private val tags = listOf("Fantasy", "Adventure")

    init {
        _state.update {
            it.copy(
                items = items,
                tags = tags,
                name = "Fantastic Beasts:The Secrets Of Dumbledore",
                description = "Professor Albus Dumbledore knows the powerful, dark wizard Gellert Grindelwald is moving to seize control of the wizarding world. Unable to stop him..."
            )
        }
    }


}